    private void Equal_ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Equal_ActionPerformed
        
        switch(a){
            case 1:
                for(int i=0 ; i<s.length(); i++){
                    if(s.charAt(i) == '+') {s1 = s.substring(0, i); s2 = s.substring(i+1, s.length()); break;}
                }
                
                int a,b;
                if (!s1.equals(null) && !s2.equals(null)){
                    if ( (s1.length() <= 8 && s2.length() <= 8) ){
                        a = Integer.parseInt(s1);
                        b = Integer.parseInt(s2);
                        a = a+b;
                        s = Integer.toString(a);
                        outp.setText(s);
                    }
                    else { EM.setText("Error : The maximum length number is 8."); s = "";}
                }
                else { EM.setText("Error : First Enter a number."); s = ""; s1 = ""; s2 = "";}
                
                
            break;
                
            case 2:
               for(int i=0 ; i<s.length(); i++){
                    if(s.charAt(i) == '-') {s1 = s.substring(0, i); s2 = s.substring(i+1, s.length()); break;}
                    
                }
               
               
               if(s1.length() <= 8 || s2.length() <= 8){
                    a = Integer.parseInt(s1);
                    b = Integer.parseInt(s2);
                    a = a-b;
                    s = Integer.toString(a);
                    outp.setText(s);
               }
               else if ( (s1.equals(null)) || (s2.equals(null)) ){ EM.setText("Error : First Enter a number."); s = ""; s1 = ""; s2 = "";}
               else {EM.setText("Error : The maximum length number is 8."); s = "";}
            break;
                
            case 3:
                for(int i=0 ; i<s.length(); i++){
                    if(s.charAt(i) == 'x') {s1 = s.substring(0, i); s2 = s.substring(i+1, s.length()); break;}
                    
                    
                }
                
                if(s1.length() <= 8 || s2.length() <= 8){
                    a = Integer.parseInt(s1);
                    b = Integer.parseInt(s2);
                    a = a*b;
                    s = Integer.toString(a);
                    outp.setText(s);
                }
                else if ( (s1.equals(null)) || (s2.equals(null)) ){ EM.setText("Error : First Enter a number."); s = ""; s1 = ""; s2 = "";}
                else {EM.setText("Error : The maximum length number is 8."); s = "";}
            break;
                
            case 4:
                for(int i=0 ; i<s.length(); i++){
                    if(s.charAt(i) == 'รท') {s1 = s.substring(0, i); s2 = s.substring(i+1, s.length()); break;}
                    
                    
                }
                if(s1.length() <= 8 || s2.length() <= 8){
                    a = Integer.parseInt(s1);
                    b = Integer.parseInt(s2);
                    a = a/b;
                    s = Integer.toString(a);
                    outp.setText(s);
                }
                else if ( (s1.equals(null)) || (s2.equals(null)) ){ EM.setText("Error : First Enter a number."); s = ""; s1 = ""; s2 = "";}
                else {EM.setText("Error : The maximum length number is 8."); s = "";}
            break;
                
        }
    }//GEN-LAST:event_Equal_ActionPerformed

